function fetch_finance_api(){
	$quote = file_get_contents("https://www.google.com/finance/info?q=UKX");
	$json = str_replace("\n", "", $quote);

	//Remove //, [ and ] to build qualified string  
	$data = substr($json, 4, strlen($json) -5);
	
	//decode JSON data
	return json_decode(utf8_decode($data));
}

function fetch_rss(){ 
	//get feed data
    $feed = file_get_contents("http://feeds.bbci.co.uk/news/business/rss.xml"); 
	
    $data = simplexml_load_string($feed);
	
    $articles = array();
	//loop through feed data to gather image and prepare format
    foreach ($data->channel->item as $item) { 
        $media = $item->children('http://search.yahoo.com/mrss/'); 
        $image = array(); 
		if($media){
			foreach($media->attributes() as $key => $value) { 
				$image[$key] = (string)$value; 
			}
		}
        $articles[] = array( 
            'title' => (string)$item->title, 
			'pubdate' => (string)$item->pubDate,
            'description' => (string)$item->description, 
            'link' => (string)$item->link, 
            'image' => $image, 
        ); 
    } 
	return $articles; 
} 
function xss_clean($strString){
	/*
	* follow McAfee recomendations to pass through checks
	* Remove < input and replace with &lt;
	* Remove > input and replace with &gt;
	* Remove ' input and replace with &apos;
	* Remove " input and replace with &#x22;
	* Remove ) input and replace with &#x29;
	* Remove ( input and replace with &#x28;
	*/
	$arrFind = array('<','>','\'','"','(',')');
	$arrReplace = array('&lt;','&gt;','&apos;','&#x22;','&#x29;','&#x28;');
	return str_replace($arrFind,$arrReplace,$strString);
}

if(!empty($_POST)){
